def build_vocab(texts): #reconstruiremos el diccionario varias veces durante el pre-procesamiento 
#para ver cambios
    sentences = texts.apply(lambda x: x.split()).values
    vocab = {}
    for sentence in sentences:
        for word in sentence:
            try:
                vocab[word] += 1
            except KeyError:
                vocab[word] = 1
    return vocab

