def clean_special_chars(text, carc_esp, mapping):
    for c in mapping:
        text = text.replace(c, mapping[c])

    for c in carc_esp:
        text = text.replace(c, f' {c} ')

    specials = {'\u200b': ' ', '…': ' ... ', '\ufeff': '', 'करना': '', 'है': ''}  
    for s in specials:
        text = text.replace(s, specials[s])

    return text

