# extracing title from json files
def parse_json(fn):
    with open(fn, 'r') as f:
        data = json.loads(f.read())
#     print(data)
    source = fn.split('/')[4]
#     print('source=',source)
    return([data['paper_id'], data['metadata']['title'], source, fn])

