def load_data():
    papers = []
    import os
    import json
    for dirname, _, filenames in os.walk('/kaggle/input'):
        for filename in tqdm(filenames):
            if '.json' not in filename:
                continue
            try:
                fn = os.path.join(dirname, filename)
#                 print(fn)
                papers.append(parse_json(fn))
            except Exception as e:
                print(e)
                continue
    return papers

