def preprocess_text_for_word_embeddings(text):    
    for word in text.split():
        if word.lower() in contractions:
            text = text.replace(word, contractions[word.lower()])    
    text = re.sub(r'\d+', '', text)
    text = re.sub('[^A-Za-z0-9]+', ' ', text)
    text = text.strip()

    return text

