def Cover_name(x):
    if x == 1:
        return 'Spruce/Fir'

    if x == 2:
        return 'Lodgepole Pine'

    if x == 3:
        return 'Ponderosa Pine'

    if x == 4:
        return 'Cottonwood/Willow'

    if x == 5:
        return 'Aspen'

    if x == 6:
        return 'Douglas-fir'

    if x == 7:
        return 'Krummholz'


data['Cover_Type'] = data['Cover_Type'].apply(Cover_name)
data['Cover_Type'].value_counts()

