def rating_group(rating):
    if rating in ['G','TV-G','TV-Y']:
        return 'Kids'

    elif rating in ['TV-Y7','TV-Y7-FV','PG','TV-PG']:
        return 'Older Kids'

    elif rating in ['PG-13','TV-14',]:
        return 'Teens'

    elif rating in ['R','TV-MA','NC-17']:
        return 'Adults'

    elif not rating or rating in ['UR','NR']:
        return 'Not Rated/Unrated'

