def grades(pass_status,total_marks):
    if pass_status == 'F':
        return 'F'
    if total_marks >=90:
        return 'A+'
    if total_marks >=80:
        return 'A'
    if total_marks >=70:
        return 'B'
    if total_marks >=60:
        return 'C'
    if total_marks >=50:
        return 'D'
    if total_marks >=40:
        return 'E'
