def conv1(s):
    res = 0
    if 'K' in s:
        res = float(s.split('K')[0].split('€')[1]) / (1000000)
    if 'M' in s:
        res = float(s.split('M')[0].split('€')[1])
    return res

def conv2(s):
    if s >= 1:
        res = '€' + str(s) + 'M'
    if s < 1:
        res = '€' + str(s * 1000000) + 'K'
    return res

# тк зарплаты представлены в виде строк со специальными символами, а их нужно отсортировать, to_numeric не сработает,
# поэтому нужно немного поработать с данными

df['Value'] = df['Value'].apply(func = conv1) # перевел зарплаты в числовые значения миллионов, чтобы отсортировать
df = df.sort_values(by = 'Value', ascending=False) # отсортировал
df['Value'] = df['Value'].apply(func = conv2) # преобразовал строки в начальное состояние

fig = px.histogram(df, x = 'Value', y = 'Skill Moves', histfunc = 'avg')
fig.update_layout(width = 2000, title = "Correlation between player's Value & Skill Moves")
