def freq_table(dataset,index):
    temp_table = {}
    total = 0
    for row in dataset:
        temp = row[index]
        total += 1
        if temp in temp_table:
            temp_table[temp] += 1
        else:
            temp_table[temp] = 1

    table_percentages = {}
    for key in temp_table:
        percent = (temp_table[key] / total) * 100
        table_percentages[key] = percent 

    return table_percentages

