def sum_mean(data,col1,col2=[]):
    sum_df, mean_df = [] ,[]
    for i in data[col1].unique():
        d = data[data[col1] == i]
        s, m = [i], [i]
        if len(col2) == 0:
            col2 = [x for x in data.columns if x != col1]
        for j in col2:
            s.append(d[j].sum())
            m.append(d[j].sum())
        sum_df.append(s)
        mean_df.append(m)
    return sum_df,mean_df
