def date_to_numbers(date):
    date_to_be_returned=''
    date=date.replace(',','')
    date=date.replace(' ','')
    if date[0:3] == "Aug":
        date_to_be_returned += str(8)
    else:
        date_to_be_returned += str(7)

    date_to_be_returned += date[3:]
    return [float(date_to_be_returned)]
