def classificacao_etaria(idade):
    if idade < 12:
        return 'Criança'
    elif idade < 18:
        return 'Adolescente'
    elif idade < 65:
        return 'Adulto'
    else:
        return 'Idoso'

df['Classificação Etária'] = df['Idade'].apply(classificacao_etaria)

