#seperating acts

crime_acts = x_full['crime against women(Act)'].astype('string').values
for i in range(1,11):
    x_full.insert(8+i,"Act {}".format(i),0)
for j in range(len(crime_acts)):
    splitted = crime_acts[j].split(',')
    for x in splitted:
        if x=='0':
            continue
        x_full.loc[int(j),'Act {}'.format(x.strip())]=1
x_full
