def map5(X, y):
    score = 0
    for i in range(X.shape[0]):
        pred = X[i].argsort()[-5:][::-1]
        for j in range(pred.shape[0]):
            if pred[j] == y[i]:
                score += (5 - j)/5
                break
    return score/X.shape[0]

best_th = 0
best_score = 0
for th in np.arange(0.1, 0.801, 0.01):
    preds[:,0] = th
    score = map5(preds, y)
    if score > best_score:
        best_score = score
        best_th = th
    print("Threshold = {:.3f}, MAP5 = {:.3f}".format(th,score))
