def json_to_dataframe(data, column):
    data_list = []

    for row in range(data.shape[0]):

        json_data = data.loc[row, column]
        if str(json_data) != "nan":
            df = pd.read_json(json_data)
            data_list.append(df)

    all_data = pd.concat(data_list, axis = 0)

    return all_data
