def check_weekend(row):
    """custom function that tells us if a certain date happened on the weekend or not"""

    if row['day_of_week'] == '0':
        val = 'yes'
    elif row['day_of_week'] == '6':
        val = 'yes'
    else:
        val = 'no'
    return val

