def fiyatKategorisiBul(ram, bGucu, cGenislik, cYukseklik):

    sonuc = model.predict([[ram, bGucu, cGenislik, cYukseklik]])

    if sonuc<1:
        return "Çok Ucuz"
    elif sonuc>=1 and sonuc<2:
        return "Ucuz"
    elif sonuc>=2 and sonuc<3:
        return "Normal"
    elif sonuc>=3:
        return "Pahalı"

