def kategori_Bul(ram, bGucu, cGenislik, cYukseklik):

    sonuc = knn_model.predict([[ram, bGucu, cGenislik, cYukseklik]])

    if sonuc==0:
        return "Çok Ucuz"
    elif sonuc==1:
        return "Ucuz"
    elif sonuc==2:
        return "Normal"
    elif sonuc==3:
        return "Pahalı"
