def has_lucky_number(nums):
    """Return whether the given list of numbers is lucky. A lucky list contains
    at least one number divisible by 7.
    """
    for num in nums:
        if num % 7 == 0:
            return True
        else:
            return False
nums = [0, 1, 2, 3, 4, 5, 6, 7, 14]
has_lucky_number(nums)

