coffees = ['아메리카노', '카페라떼', '카페모카', '바닐라라떼', '핸드드립', '콜드브루']
prices = [4100, 4600, 4600, 5100, 6000, 5000]

# 1)
print("# 1")
for i in range(len(coffees)):
    if prices[i]-500 <= 5000:
        print(coffees[i])

# 2)
print("# 2")
for i, price in enumerate(prices): #(0, 4100)
    if price-500 <= 5000:
        print(coffees[i])

# 3)
print("# 3")
for coffee, price in zip(coffees, prices): # ('아메리카노', 4100)
    if price-500 <= 5000:
        print(coffee)

