def extract_words(text):
    text = text.replace('nan', '').replace('\r', '').replace('\n', '').replace('<p>', '').replace('  ', '')
    text = re.sub(r'<a.*</a>', "", text)
    words = text.split(' ')

    for i in range(len(words)):
        if len(words[i]) > 0:
            if words[i][-1] in ['.', ',', ':']:
                words[i] = words[i][:-1]
        words[i] = words[i].lower()

    return words
