with open('../input/amazon_jobs_dataset.csv', encoding='utf8') as file_obj:
    file_data=csv.DictReader(file_obj, skipinitialspace=True)
    lst=list(file_data)
    l=[]
    for row in lst:
        a=row['Posting_date'].split()
        if '2018' in a:
            l.append(a)
dic={}
for i in l:
    if i[0] in dic.keys():
        dic[i[0]]+=1
    else:
        dic[i[0]]=1
freq=0
maxmonth='January'
for i in dic:
    if dic[i]>freq:
        freq=dic[i]
        maxmonth=i
print(maxmonth, freq)

