with open('../input/amazon_jobs_dataset.csv', encoding='utf8') as file_obj:
    file_data=csv.DictReader(file_obj, skipinitialspace=True)
    lst=list(file_data)
    dic={}
    for row in lst:
        a=row['BASIC QUALIFICATIONS']
        b=row['location'].split(",")[0]
        if "Java" in a:
            if b in dic.keys():
                dic[b]+=1
            else:
                dic[b]=1

count=0
country=""
for i in dic:
    if dic[i]>count:
        count=dic[i]
        country=i
print(country, count)
