#storing all the pairs to find relation between two acts in a 2 dimensional array
arr = [[0 for i in range(0,11)] for j in range(0,11)]
for i in crimes:
  if type(i)==list:
    for j in range(0,len(i)-1):
      for k in range(j+1,len(i)):
        y=i[j]-1
        z=i[k]-1
        arr[y][z]=arr[y][z]+1
        arr[z][y]=arr[z][y]+1
print(arr)

