def cloudMap(data):
    if("Scattered Clouds" in data or "Partly Cloudy" in data):
        return 3/8
    elif("Mostly Cloudy" in data):
        return 6/8
    elif("Cloudy" in data or "Overcast" in data):
        return 1
    return 0

def clearMap(data):
    if("Clear" in data ):
        return 1
    return 0


def fairMap(data):
    if("Fair" in data ):
        return 1
    return 0

def rainMap(data):
    if("Light Rain" in data or "Light Drizzle" in data):
        return .25
    elif("Light Thunderstorms and Rain" in data or "Heavy Rain" in data or "Squalls" in data):
        return 1
    elif("Rain" in data):
        return .5
    return 0

def hazeMap(data):
    if("Haze" in data):
        return 1
    return 0


def windyMap(data):
    if("Windy" in data or "Squalls" in data):
        return 1
    return 0

def fogMap(data):
    if("Shallow Fog" in data):
        return .5
    elif("Patches of Fog" in data):
        return 1

    elif("Mist" in data or "Fog" in data or "Smoke" in data):
        return .25
    return 0

