def create_data_loader(data, tokenizer, max_len, batch_size):
  ds = IMDBDataset(
    reviews=data.review.to_numpy(),
    sentiments=data.sentiment.to_numpy(),
    tokenizer=tokenizer,
    max_len=max_len
  )

  return DataLoader(
    ds,
    batch_size=batch_size,
    num_workers=4
  )

