def get_label_representation(dataframe, labels_name):
    num_labels = sorted(set((dataframe[labels_name].values)))
    label_count = {}
    for i,x in enumerate(num_labels):
        label_count[x] = len(dataframe.loc[dataframe[labels_name] == x])
    return label_count

