#Question11:find the first 5 repetitive family name
def select_family_name(myList):
    if myList[0] in ['Mrs.','Miss.','Mr.','Dr.']:
        return myList[2]
    else:
        return myList[1]

df['family_name']=df['name'].str.split().apply(select_family_name)
pd.Series(df.groupby('family_name').count().sort_values('name',ascending=False).iloc[0:5].index)
