for col in AnnualTicketSales.columns:
    if type(AnnualTicketSales[col][0]) == str:
        AnnualTicketSales[col] = AnnualTicketSales[col].str.replace('$', '')
        AnnualTicketSales[col] = AnnualTicketSales[col].str.replace(',', '')
        if col == 'AVERAGE TICKET PRICE':
            AnnualTicketSales[col] = AnnualTicketSales[col].astype(float)
        else:
            AnnualTicketSales[col] = AnnualTicketSales[col].astype(int)

