def preprocess_image(image_path, desired_size=224):
    im = Image.open(image_path)
    im = im.resize((desired_size, desired_size))
    im = np.array(im)
    if len(im.shape)==3:
        return im
    else:
        im2 = np.empty((desired_size, desired_size, 3), dtype=np.uint8)
        for j in range(3):
            im2[:,:,j] = im
        return im2

