"""

class Perceptron:
    def __init__(self):
        self.w = None
        self.b = None

    def model(self,x):
        return 1 if np.dot(self.w,x) >= self.b else 0

    def predict(self, X):
        Y = []
        for x in X:
            result = self.model(x)
            Y.append(result)
        return np.array(Y)

    def fit(self, X, Y, epochs = 1, lr = 1):
        self.w = np.ones(X.shape[1])
        self.b = 0

        accuracy = {}
        max_accuracy = 0
        wt_matrix = []

        for i in range(epochs):
            for x,y in zip(X, Y):
                ypred = self.model(x)
                if y == 1 and ypred == 0:
                    self.w += lr * x
                    self.b += lr * 1
                elif y == 0 and ypred == 1:
                    self.w -= lr * x
                    self.b -= lr * 1

            wt_matrix.append(self.w)
            accuracy[i] = accuracy_score(self.predict(X),Y)

            if max_accuracy<accuracy[i]:
                max_accuracy = accuracy[i]
                chkptw = self.w
                chkptb = self.b

        self.w = chkptw
        self.b = chkptb

        print("Max Accuracy : ",max_accuracy)
        if epochs > 1:
            plt.plot(list(accuracy.values()))
            plt.show()

        return np.array(wt_matrix)

