def convert_stars(row):
    stars = 0
    if 'Canada' == row.country:
        stars= 3
    elif row.points >= 95:
        stars= 3
    elif row.points >= 85:
        stars= 2
    else:
        stars= 1
    return stars

star_ratings = reviews.apply(convert_stars, axis='columns')

