def create_folder(path):
    if not os.path.exists(path):
        os.makedirs(path)


def write_bboxes_to_ann(file, bboxes):
    for j in range(len(bboxes)):
        text = ' '.join(bboxes[j])
        file.write(text)
        file.write("\n")


