# Dealing with incorrect entries.
def convert(x):
    if x== 'SeedFunding':
        return 'Seed Funding'
    elif x== 'PrivateEquity':
        return 'Private Equity'
    elif x== 'Crowd funding':
        return 'Crowd Funding'
    else:
        return x

df_startup['InvestmentnType'] = df_startup['InvestmentnType'].apply(convert)

