def year_segment(x):
    if x is None:
        return x
    elif (x) < 1910:
        return 'before 1910'
    elif (x) < 1930:
        return '1910 to 1930'
    elif (x) < 1950:
        return '1930 to 1950'
    elif (x) < 1970:
        return '1950 to 1970'
    elif (x) < 1990:
        return '1970 to 1990'
    elif (x) < 2010:
        return '1990 to 2010'
    elif (x) >= 2010:
        return 'after 2010'


data['year_segment'] = data['founded_year'].apply(lambda x: year_segment(x))

