def merchant_pop_on_prod(m_count,p_count):
    if m_count == 0:
        return 0
    else:
        return p_count / m_count

salesData['merch_pop_proc'] = salesData.apply(lambda x: merchant_pop_on_prod(x.merchant_rating_count, x.rating_count),
                                              axis = 1)
