def city_cleaner(string, 
                 cities=["lahore","karachi","faisalabad","islamabad",
                        "rawalpindi", 'peshawar','nowshera',"gujrat"
]
):
    for city in cities:
        if city in string:
            string = city
    return string
print("Function Defined")
startup_pk["locations"] = startup_pk.Location.str.lower().apply(city_cleaner)
