def Rhythm(value):
    if value <= 85:
        return 'Adagio'
    elif value > 86 and value <= 100:
        return 'Andante'
    elif value>101 and value<=120:
        return 'Allegretto'
    elif value > 120 and value <= 156:
        return 'Allegro'
    elif value > 156 and value <= 176:
        return 'Vivace'
    elif value>176:
        return 'Presto'
