class perceptron:

    def __init__(self):
        self.w = None
        self.b = None

    def model(self,x):
        return 1 if (np.dot(self.w, x) >= self.b) else 0 

    def predict(self,x):
        y = []
        for row in x:
            result = self.model(row)
            y.append(result)
        return np.array(y)

    def fit(self,x,y, epochs = 1, lr = 0.001):
        self.w = np.ones(x.shape[1])
        self.b = 0

        accuracy = {}
        max_accuracy = 0

        for i in range(epochs):
            for X,Y in zip(x_train, y_train):
                y_pred = self.model(X)
                if Y == 1 and y_pred == 0:
                    self.w = self.w + lr*X
                    self.b = self.b + lr*1
                elif Y == 0 and y_pred == 1:
                    self.w = self.w - lr*X
                    self.b = self.b - lr*1

            accuracy[i] = accuracy_score(self.predict(x),y)
            if (accuracy[i]> max_accuracy):
                max_accuracy =accuracy[i]
                checkpoint_w = self.w
                checkpoint_b = self.b

        self.w = checkpoint_w
        self.b = checkpoint_b

        print(max_accuracy)
        plt.plot(accuracy.values(),data = accuracy)
        plt.ylim(0,1)
        plt.show()
