def accuracy(y_more_toxic, y_less_toxic): 
    a = torch.sum(torch.where(y_more_toxic>y_less_toxic, 1, 0))
    b = torch.sum(torch.where(y_more_toxic>y_less_toxic, 0, 1))
    return a / (a + b)
