def fn(x):
    if x!='Free' and x!='NaN':
        #print(x)
        x=str(x)
        if '₹' in x:
            x=x.split('₹')[1]
        if ',' in x:       
            x,y=x.split(',')
            x=x+y
        x=float(x)
        return x
    else:
        return 0

