class Chrono:
    def __init__(self):
        self.chrono = {}
        self.acc_time = {}

    def start(self, name):
        self.chrono[name] = datetime.datetime.now().timestamp()

    def stop(self, name):
        timestop = datetime.datetime.now().timestamp() - self.chrono[name]
        if name in self.acc_time.keys():
            self.acc_time[name] += timestop
        else:
            self.acc_time[name] = timestop
