def generate_fibo(limit=100):
    # bevat een tuple (lijst) van 2 getallen
    pointer = (0,1)
    for i in range(limit):
        if i == 0:
            yield 0
        if i == 1:
            yield 1

        first, second = pointer
        pointer = (pointer[1], first + second)
        yield first + second

