result = []
with open('generated-puisi.txt', 'r') as f:
    lines = f.readlines()
    for line in lines:
        if line.startswith('<BOS>'):
            result.append(line[5:].replace(r'\n', '\n'))

for idx, puisi in enumerate(result):
    print(" ===========\n   Puisi {}\n ===========\n".format(idx))
    print(puisi)
