def remove_punctuation(title):
    punc = '!()-[]{};:\'"\,<>/?@#$%^&*_~\'0123456789+abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ' 
    for ele in title:  
        if ele in punc:  
            title = title.replace(ele, "")
    return title

