def piv(piv_col, val_col, df=engage_df, rolling=False, title=None, facet=False):
    if facet:
        pivs = (
            df
            .pivot_table(
                index=["time", facet], 
                columns=piv_col, 
                values=val_col, 
                aggfunc="mean"
            )
        )
    else:
        pivs = (
            df
            .pivot_table(
                index="time", 
                columns=piv_col, 
                values=val_col, 
                aggfunc="mean"
            )
        ) 

    if rolling:
        pivs = pivs.rolling(rolling).mean()

    if facet:
        return px.line(
            pivs.reset_index(), 
            x="time", 
            y=pivs.columns,
            facet_col=facet,
            facet_col_wrap=1,
            title=title
        )
    else:
        return px.line(
            pivs.reset_index(), 
            x="time", 
            y=pivs.columns,
            title=title
        )
