def normalize(msg):

    doc = nlp(msg)

    res=[]

    #for sent in doc.sents:
     #   print(sent.text)

    for token in doc:


        if(token.is_stop or token.is_digit or token.is_punct or not(token.is_oov)):

            pass

        else:

            res.append(token.lemma_.lower())

    return res

