non_alphanums = re.compile(u'[^A-Za-z0-9]+')

def cln_txt(doc):
    new = []
    for txt in doc.split():
        txt = txt.lower()
        if txt in stop_words:
            continue 
        txt = non_alphanums.sub("", txt)
        if txt == "":
            continue 
        new.append(txt)
    return " ".join(new)

q["question"] = q["question"].apply(cln_txt)

