def create_word(df: pd.DataFrame) -> Dict[str, int]:
    word2count = {}
    for doc in df:
        for word in doc.split():
            if word not in word2count:
                if word in ["select", "selected", "use", "apply", "choice"]: 
                    continue 
                word2count[word] = 1 
            else:
                word2count[word] += 1 
    return word2count

